using System;
using Gtk;

namespace ${Namespace}
{
	class MainClass
	{
		public static void Main (string[] args)
		{
			Application.Init ();
			Builder builder = new Builder (null, "${Namespace}.interfaces.MainWindow.ui",null);
			MainWindow win = new MainWindow (builder, builder.GetObject ("window1").Handle);
			win.Show ();
			Application.Run ();
		}
	}
}

using System;
using Gtk;
using UI = Gtk.Builder.ObjectAttribute; 

public partial class MainWindow: Gtk.Window
{

	[UI] Gtk.Button button1;
	[UI] Gtk.Label label1;

	int clickedTimes;

	public MainWindow (Builder builder, IntPtr handle): base (handle)
	{
		builder.Autoconnect (this);
		DeleteEvent += OnDeleteEvent;

		button1.Clicked += onButtonClick;
	}

	protected void onButtonClick (object sender, EventArgs a)
	{
		clickedTimes++;
		label1.Text = string.Format ("Hello World! This button has been clicked {0} time(s).", clickedTimes);
	}

	protected void OnDeleteEvent (object sender, DeleteEventArgs a)
	{
		Application.Quit ();
		a.RetVal = true;
	}
}
